/*!50003 CREATE*/ /*!50017 DEFINER=`improov`@`10.%`*/ /*!50003 TRIGGER calcula_custo_fixo
AFTER INSERT ON imagens_cliente_obra
FOR EACH ROW
BEGIN
    DECLARE total_custo_fixo DECIMAL(10, 2);

    -- Calcula o custo fixo total para a obra da nova imagem
    SELECT SUM(precos_tipo_imagem.custo_fixo_por_imagem * tipo_imagem_count.qtde)
    INTO total_custo_fixo
    FROM (
        SELECT tipo_imagem, COUNT(*) AS qtde
        FROM imagens_cliente_obra
        WHERE obra_id = NEW.obra_id
        GROUP BY tipo_imagem
    ) AS tipo_imagem_count
    JOIN precos_tipo_imagem ON precos_tipo_imagem.tipo_imagem = tipo_imagem_count.tipo_imagem;

    -- Verifica se o registro da obra já existe na tabela custos_fixos_obra
    IF NOT EXISTS (
        SELECT 1
        FROM custos_fixos_obra
        WHERE obra_id = NEW.obra_id
    ) THEN
        -- Insere um novo registro para a obra
        INSERT INTO custos_fixos_obra (obra_id, custo_fixo)
        VALUES (NEW.obra_id, total_custo_fixo);
    ELSE
        -- Atualiza o custo fixo na tabela custos_fixos_obra
        UPDATE custos_fixos_obra
        SET custo_fixo = total_custo_fixo
        WHERE obra_id = NEW.obra_id;
    END IF;
END */;;
DELIMITER ;
